// hide menu bar on click

function hideMenu(){
    var navbar = document.getElementsByClassName("nav-xbootstrap mr-auto")[0]
    var cross = document.getElementsByClassName("navbar-xbootstrap")[0]
    navbar.classList.remove("visible");
    cross.classList.remove("change");
} 

